import React from "react";
import FormInput from "./FormInput";

class AddCar extends React.Component {
    render() {
        return (
            <div className="container">
                <div className="col">
                    <form onSubmit={this.handleFormSubmit}>
                        <div className="form-group">
                            <h1>Add a new Car</h1>
                            <FormInput id="albumTitle" title="Album Title" placeholder="Enter Title" onChange={this.updateTitle}/>
                            <FormInput id="albumArtist" title="Artist" placeholder="Enter Artist" onChange={this.updateArtist}/>
                            <FormInput id="albumYear" title="Year" placeholder="Enter Year" onChange={this.updateYear}/>
                            <FormInput id="albumImage" title="Image" placeholder="Enter Image" onChange={this.updateImage}/>
                        </div>
                        <button type="submit" className="btn btn-light">Cancel</button>
                        <button type="submit" className="btn btn-primary">Submit</button>
                    </form>
                </div>
            </div>
        )
    }
}
export default AddCar;
